import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  statusLogUp: "",
  isLoading: true,
  error: "",
};

const logUpSlice = createSlice({
  name: "logUp",
  initialState,
  reducers: {
    getStart: (state) => {
      state.isLoading = true;
    },
    getSuccess: (state, action) => {
      state.isLoading = false;
      state.statusLogUp = action.payload;
    },
    getError: (state, action) => {
      state.isLoading = false;
