import React, { useContext, useState, useMemo, useEffect } from "react";
import { LogUpContext } from "../../contexts/LogUp.Context";
import clsx from "clsx";
import bannerForm from "../../assets/images/banners/bannerForm.png";
import fbIcon from "../../assets/icons/fb.png";
import ggIcon from "../../assets/icons/gg.png";
import { useNavigate, useLocation } from "react-router-dom";
import { useRef } from "react";
import { checkOtp } from "../../reducers/account/logUp.Reducer";

export default function EnterOtp({ sendOtp, checkOtp, statusEnterOtp }) {
  const [secondsTime, setSecondsTime] = useState(5);
  const [minuteTime, setMinuteTime] = useState(1);
  const [checkTime, setCheckTime] = useState(false);
  const secondsRef = useRef();
  const minuteRef = useRef();

  const logUpContext = useContext(LogUpContext);

  const navigate = useNavigate();

  // handle countdown in form otp
  useEffect(() => {
    if (!localStorage.getItem("enterMail")) {
      navigate("/enterMail");
    }
  });

  const countdown = () => {
        if(minuteTime === 0 && secondsTime === "00"){
      setCheckTime(true);
    }
    if (secondsTime != 0) {
      if (secondsTime < 11) {
        const temp = secondsTime - 1;
        setTimeout(() => {
          setSecondsTime("0" + temp);
        }, 1000);
        // console.log(secondsTime)
      } else {
        setTimeout(() => {
          setSecondsTime((pre) => pre - 1);
        }, 1000);
      }
    } else {
      if (minuteTime != 0) {
        setTimeout(() => {
          setSecondsTime(10);
          setMinuteTime((pre) => pre - 1);
          // console.log("setMinuteTime");
        }, 1000);
      }
    }
    // console.log(minuteTime + ":" + secondsTime);
  };

  // useEffect(() => {
  //   if(minuteTime === 0 && secondsTime === "00"){
  //     setCheckTime(true);
  //   }
  //   if (secondsTime != 0) {
  //     if (secondsTime < 11) {
  //       const temp = secondsTime - 1;
  //       setTimeout(() => {
  //         setSecondsTime("0" + temp);
  //       }, 1000);
  //       // console.log(secondsTime)
  //     } else {
  //       setTimeout(() => {
  //         setSecondsTime((pre) => pre - 1);
  //       }, 1000);
  //     }
  //   } else {
  //     if (minuteTime != 0) {
  //       setTimeout(() => {
  //         setSecondsTime(10);
  //         setMinuteTime((pre) => pre - 1);
  //         // console.log("setMinuteTime");
  //       }, 1000);
  //     }
  //   }
  //   // console.log(minuteTime + ":" + secondsTime);
  // });

  // resend OTP
  const resendOtp = () => {
    sendOtp({ email: localStorage.getItem("email") });
    setCheckTime(false);
    setMinuteTime(2);
    setSecondsTime(59);
  };

  const handleOtp = (otp) => {
    if (!otp) {
      logUpContext.setCheckOtp(false);
    } else {
      logUpContext.setCheckOtp(true);
    }
  };

  const validateOtp = (otp) => {
    console.log("otp: " + otp);
    if (otp === undefined) {
      logUpContext.setCheckOtp(false);
    }
    logUpContext.setCheckOtp(true);
    // console.log("otp: " + logUpContext.otp);
    // console.log("email: " + localStorage.getItem("email"));
    checkOtp({ email: localStorage.getItem("email"), otp: logUpContext.otp });
    console.log("status from component: " +  statusEnterOtp);
    if (statusEnterOtp === "valid") {
      localStorage.setItem("enterOtp", false);
      navigate("/logUp");
    }
    if (statusEnterOtp === "expire") {
      sendOtp({ email: localStorage.getItem("email") });
    }
  };

  return (
    <div className="main" style={{ backgroundColor: "#f8f9fd" }}>
      {/* {console.log("component render: " + secondsTime.current)} */}
      {/* {console.log("check Otp: " + logUpContext.checkOtp)} */}
      <div className="form-pro">
        <div className="form-vip">
          <div className="banner">
            <img src={bannerForm} alt="banner-from"></img>
          </div>
          <div className="form-detail">
            <h2>Xin hãy nhập Otp</h2>
            <form
              className={clsx("needs-validation", {
                "was-validated": logUpContext.checkOtp,
              })}
              noValidate
              onSubmit={(e) => logUpContext.onSubmitEvent(e)}
            >
              <div className="form-group">
                <label htmlFor="Tên đầy đủ">Nhập Otp:</label>
                <input
                  type="text"
                  className="form-control"
                  id="text"
                  placeholder="Nhập otp"
                  name="text"
                  required
                  onChange={(e) => {
                    logUpContext.setOtp(e.target.value);
                    // console.log(logUpContext.otp);
                  }}
                />
                <div className="valid-feedback">Valid.</div>
                <div className="invalid-feedback">Vui lòng điền thông tin.</div>
              </div>
              <div className="time-countdown">
                {statusEnterOtp === "expire" && (
                  <p>Mã OTP đã hết hiệu lực, vui lòng kiểm tra lại mã mới</p>
                )}
                {statusEnterOtp === "invalid" && (
                  <p>Mã OTP chưa đúng, vui lòng thử lại</p>
                )}
                <p>
                  Bạn chưa nhận được mã? Vui lòng chờ {minuteTime}:{secondsTime}
                  s để gửi lại
                </p>
                {/* <p>
                  Vui lòng chờ {minuteTime}:{secondsTime} để gửi lại
                </p> */}
                {minuteTime === 0 && secondsTime === "00" && (
                  <p
                    onClick={(e) => {
                      resendOtp();
                    }}
                    style={{
                      textDecoration: "underline",
                      color: "#0275ff",
                      cursor: "pointer",
                    }}
                  >
                    Gửi lại OTP
                  </p>
                )}
              </div>
              <button
                style={{ width: "100%", color: "white" }}
                type="submit"
                className="btn btn-vip"
                onClick={() => {
                  // sendOtpWithConditional();
                  validateOtp(logUpContext.otp);
                  // sendOtp({"otp:" :})
                  // console.log(logUpContext.otp);
                }}
              >
                <i className="fas fa-arrow-right" /> Nhập OTP
              </button>
              <div className="interaction">
                <h6>Đăng nhập với</h6>
                <div className="social">
                  <img src={ggIcon} alt=""></img>
                  <img src={fbIcon} alt=""></img>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
