import React, {
  useEffect,
  useLayoutEffect,
  useContext,
  useMemo,
  useState,
} from "react";
import EnterMail from "./EnterMail.Component";
import EnterOtp from "./EnterOtp.Component";
import LoadingPage from "../LoadingPage.Component";
import { LogUpContext } from "../../contexts/LogUp.Context";
import clsx from "clsx";
import bannerForm from "../../assets/images/banners/bannerForm.png";
import fbIcon from "../../assets/icons/fb.png";
import ggIcon from "../../assets/icons/gg.png";
import { logUp } from "../../reducers/account/logUp.Reducer";

export default function LogUp({ statusLogUp, sendOtp, checkOtp }) {
  const logUpContext = useContext(LogUpContext);

  const checkSpecialChar = (text) => {
    var format = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+/;
    if (format.test(text)) {
      // console.log(true);
      return true;
    } else {
      // console.log(false);
      return false;
    }
  };

  const handleLogUpForm = (e) => {
    e.preventDefault();
    if (logUpContext.fullName == "") {
      logUpContext.setCheckFillFullName(false);
    }
    if (logUpContext.pass == "") {
      logUpContext.setCheckFillPass(false);
    }
    if (logUpContext.fullName == "") {
      logUpContext.setCheckFillRepass(false);
    }
  };

  useEffect(() => {
    logUpContext.setCheckSpecialInFullName(
      checkSpecialChar(logUpContext.fullName)
    );
    // setCheckSpecialInFullName(checkSpecialChar(fullName));
  }, [logUpContext.fullName]);

  useEffect(() => {
    // logUpContext.checkPassCoinCide();
    // console.log("check pass and repass: " + logUpContext.coincidePass);
    if (logUpContext.pass != logUpContext.rePass) {
      logUpContext.setCoincidePass(false);
    } else {
      logUpContext.setCoincidePass(true);
    }
  }, [logUpContext.rePass]);

  return (
    <div>
      {console.log("coin cidePass: " + logUpContext.coincidePass)}
      {console.log("pass: " + logUpContext.pass)}
      {console.log("repass: " + logUpContext.rePass)}
      <div className="main" style={{ backgroundColor: "#f8f9fd" }}>
        <div className="form-pro">
          <div className="form-vip">
            <div className="banner">
              <img src={bannerForm} alt="banner-from"></img>
            </div>
            <div className="form-detail">
              <h2>
                Đăng Ký <i className="far fa-grin-hearts" />
              </h2>
              <form
                className={clsx({
                  "form-validation-invalid":
                    logUpContext.checkSpecialInFullName,
                })}
                // noValidate
                // onSubmit={(e) => onSubmitEvent(e)}
              >
                <div className="form-group">
                  <label htmlFor="Tên đầy đủ">Tên đầy đủ:</label>
                  <input
                    type="Tên đầy đủ"
                    className="form-control"
                    id="Tên đầy đủ"
                    placeholder="Nhập tên đầy đủ"
                    name="Tên đầy đủ"
                    required
                    onChange={(e) => {
                      logUpContext.setFullName(e.target.value);
                    }}
                  />
                  <div className="valid-feedback">Valid.</div>
                  {logUpContext.checkFillFullName && (
                    <div className="invalid-feedback">
                      Vui lòng điền thông tin.
                    </div>
                  )}
                  {logUpContext.checkSpecialInFullName && (
                    <div className="invalid-feedback">
                      Họ tên không được chứa ký tự đặc biệt.
                    </div>
                  )}
                  {!logUpContext.checkFillFullName && (
                    <div className="invalid-feedback">
                      Vui lòng nhập thông tin.
                    </div>
                  )}
                </div>
              </form>
              <form
                className={clsx({
                  "form-validation-invalid": !logUpContext.coincidePass,
                })}
                noValidate
                // onSubmit={(e) => onSubmitEvent(e)}
              >
                <div className="form-group">
                  <label htmlFor="pwd">Mật khẩu:</label>
                  <input
                    type="password"
                    className="form-control"
                    id="pwd"
                    placeholder="Enter password"
                    name="pwd"
                    onChange={(e) => {
                      logUpContext.setPass(e.target.value);
                    }}
                    required
                  />
                  <div className="valid-feedback">Valid.</div>
                  {/* {checkFillPass && (
                    <div className="invalid-feedback">
                      Vui lòng điền thông tin.
                    </div>
                  )} */}
                  {!logUpContext.coincidePass && (
                    <div className="invalid-feedback">Mật khẩu không khớp.</div>
                  )}
                </div>
              </form>
              <form
                className={clsx({
                  "form-validation-invalid": !logUpContext.coincidePass,
                })}
                noValidate
                // onSubmit={(e) => onSubmitEvent(e)}
              >
                <div className="form-group">
                  <label htmlFor="re-pwd">Nhập lại mật khẩu:</label>
                  <input
                    type="password"
                    className="form-control"
                    id="re-pwd"
                    placeholder="Nhập lại mật khẩu"
                    name="re-pwd"
                    onChange={(e) => {
                      logUpContext.setRePass(e.target.value);
                    }}
                    required
                  />
                  <div className="valid-feedback">Valid.</div>
                  {/* {checkFillRepass && (
                    <div className="invalid-feedback">
                      Vui lòng điền thông tin.
                    </div>
                  )} */}
                  {!logUpContext.coincidePass && (
                    <div className="invalid-feedback">Mật khẩu không khớp.</div>
                  )}
                </div>
