import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
} from "reactstrap";
import logo from "../../src/logo.png";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
