      name: "Sản Phẩm",
      link: "/product",
      icon: "fas fa-hard-hat",
    },
    {
      name: "Đại Lý",
      link: "/agency",
      icon: "far fa-building",
    },
    {
      name: "Chinh Sách",
      link: "/policy",
      icon: "far fa-paper-plane",
    },
    {
      name: "Tuyển Dụng",
      link: "/recruit",
      icon: "fas fa-user-plus",
    },
    {
      name: "Liên Hệ",
      link: "/contact",
      icon: "fas fa-phone",
    },
    {
      name: "Blogs",
      link: "/blogs",
      icon: "fas fa-blog",
    },
