import React, { useState, useEffect } from "react";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
} from "reactstrap";
import logo from "../../src/logo.png";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import classNames from "classnames";

export default function Header() {
  const navItem = [
    {
      name: "Về Chúng Tôi",
      link: "/about",
      icon: "fas fa-user-circle",
    },
    {
      name: "Sản Phẩm",
      link: "/product",
      icon: "fas fa-hard-hat",
    },
    {
      name: "Đại Lý",
      link: "/agency",
      icon: "far fa-building",
    },
    {
      name: "Chinh Sách",
      link: "/policy",
      icon: "far fa-paper-plane",
    },
    {
      name: "Tuyển Dụng",
      link: "/recruit",
      icon: "fas fa-user-plus",
    },
    {
      name: "Liên Hệ",
      link: "/contact",
      icon: "fas fa-phone",
    },
    {
      name: "Blogs",
      link: "/blogs",
      icon: "fas fa-blog",
    },
  ];
  const [isOpen, setIsOpen] = useState(true);
  const toggle = () => setIsOpen(!isOpen);

  const [styleNav, setStyleNav] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth <= 768) {
        setIsOpen(true);
      }
    };
    window.addEventListener("resize", handleResize);
  }, []);

  return (
    <div>
      <div className="header">
        <div
          style={{ backgroundColor: "rgb(26, 148, 255)" }}
          className="container-fluid header"
        >
          <div className="container">
            <div className="nav-desk">
              <div className="logo">
                {/* <img src={logo} alt="" /> */}
                <h4
                  style={{
                    border: "2px solid",
                    borderRadius: "5px",
                    padding: "5px",
                  }}
                >
                  HelmetLogo
                </h4>
              </div>
              <form className="search">
                <input type="text" placeholder="Search..."></input>
                <button>Search</button>
              </form>
              <div className="right-action">
                <div className="account">
                  <i className="fas fa-user" />
                  <div className="information">
                    <p>Tài Khoảng</p>
                    <h5>Trần Thúc Kháng</h5>
                  </div>
                </div>
                <div className="account">
                  <i className="fas fa-shopping-cart" />
                  <div className="cart">
                    <p>Giỏ Hàng</p>
                    <h5>Số Lượng: 5</h5>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <div className="container-fluid">
          <div className="container">
            <ul className="nav-main">
              {navItem.map((i) => (
                <li>
                  <h6>
                    {" "}
                    <i className={i.icon} />
                  </h6>
                  <h6>{i.name}</h6>
                </li>
              ))}
            </ul>
          </div>
        </div>

        {/* mobile */}

        <div className="container-fluid nav-bot">
          <div className="container">
            <ul className="nav-main-mobile-bot">
              {navItem.map((i) => (
                <li>
                  <h6>
                    {" "}
                    <i className={i.icon} />
                  </h6>
                  <h6>{i.name}</h6>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <div id="nav-main active" className="container-fluid" style={{backgroundColor: "pink"}}>
        <Navbar className="container-fluid" light expand="md">
          <div className="container">
                      <NavbarBrand href="/">
            {/* <Link to="/">App Exam</Link> */}
            StockMar
          </NavbarBrand>
          </div>
          <NavbarToggler
            style={{
              backgroundImage: "url(../assets/images/menu-mobile-icon)",
            }}
            onClick={toggle}
          />
          <Collapse id="nav-item" isOpen={!isOpen} navbar style={{backgroundColor: "blue"}}>
            <Nav className="mr-auto" navbar>
              <NavItem>
                <NavLink href="/technique">Tra Cứu Kỹ Thuật</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="/basic">Tra Cứu Cơ Bản</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="/basic">Tín hiệu thị trường</NavLink>
              </NavItem>
              <NavItem>
                <NavLink href="/basic">Thống kê trong phiên</NavLink>
              </NavItem>
            </Nav>
          </Collapse>
        </Navbar>
      </div>
    </div>
  );
}
