import React, {useState, useEffect} from "react";
import { Button, FormGroup, Label, Input } from "reactstrap";
import { Form, Field } from "react-final-form";

const onSubmit = (values) => {
  console.log(values);
};
export default function PostMotel() {
  const [title, setTitle] = useState();
  const [phone, setPhone] = useState();
  const [price, setPrice] = useState();
  const [square, setSquare] = useState();
  const [address, setAdress] = useState();
  const [des, setDes] = useState();
  const [img, setImg] = useState();

  document.title = "Đăng phòng";
  return (
    <div className="post-motel container">
      <div className="post-motel-form">
        <h2 style={{ textAlign: "center" }}>Đăng phòng trọ</h2>
        <div className="">
          <Form
            onSubmit={onSubmit}

            render={({
              handleSubmit,
              values,
              submitting,
              validating,
              valid,
            }) => (
              <form onSubmit={handleSubmit}>
                <FormGroup>
                  <Label for="Tiêu đề">Tiêu đề</Label>
                  <Field name="title">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="text"
                          placeholder="Tiêu đề bài viết..."
                          value={title}
                          onChange={(e) => setTitle(e.target.value)}
                          // invalid
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>
                <FormGroup>
                  <Label for="phone">Số điện thoại</Label>
                  <Field name="phone">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="phone"
                          placeholder="Số điện thoại..."
                          value={phone}
                          onChange={(e) => setPhone(e.target.value)}
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>
                <FormGroup>
                  <Label for="price">Giá</Label>
                  <Field name="price">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="text"
                          placeholder="Giá..."
                          invalid={meta.error && meta.touched}
                          value={square}
                          onChange={(e) => setSquare(e.target.value)}
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>
                <FormGroup>
                  <Label for="square">Diện tích</Label>
                  <Field name="square">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="text"
                          placeholder="Diện tích..."
                          invalid={meta.error && meta.touched}
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>
                <FormGroup>
                  <Label for="address">Địa chỉ</Label>
                  <Field name="fname">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="text"
                          placeholder="Địa chỉ..."
                          invalid={meta.error && meta.touched}
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>
                <FormGroup>
                  <Label for="fname">Mô tả</Label>
                  <Field name="fname">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="text"
                          placeholder="Mô tả..."
                          invalid={meta.error && meta.touched}
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>

                <FormGroup>
                  <Label for="des">Ảnh mô tả</Label>
                  <Field name="des">
                    {({ input, meta }) => (
                      <div>
                        <Input
                          {...input}
                          type="file"
                          invalid={meta.error && meta.touched}
                        />
                        {meta.error && meta.touched && (
                          <span>{meta.error}</span>
                        )}
                      </div>
                    )}
                  </Field>
                </FormGroup>
                <Button type="submit" color="primary" disabled={!valid}>
                  Thêm post mới
                </Button>
              </form>
            )}
          />
        </div>
      </div>
    </div>
  );
}
