import React, { useContext, useState, useMemo, useEffect } from "react";
import { LogUpContext } from "../../contexts/LogUp.Context";
import clsx from "clsx";
import bannerForm from "../../assets/images/banners/bannerForm.png";
import fbIcon from "../../assets/icons/fb.png";
import ggIcon from "../../assets/icons/gg.png";
import { useNavigate, useLocation } from "react-router-dom";
import { useRef } from "react";

export default function EnterOtp({ sendOtp }) {
  const [seccondsTime, setSeccondsTime] = useState(59);
  const [minuteTime, setMinuteTime] = useState(2);
  const seccondsRef = useRef();
  const minuteRef = useRef();

  const logUpContext = useContext(LogUpContext);

  const navigate = useNavigate();

  console.log("enterMail yet? " + localStorage.getItem("enterMail"));

  useEffect(() => {
    console.log("enterMail: " + localStorage.getItem("enterMail"));
    if (!localStorage.getItem("enterMail")) {
      navigate("/enterMail");
    }
  });
  useEffect(() => {
    if (seccondsTime > 0) {
      seccondsRef.current = setInterval(() => {
        setSeccondsTime((preSeccondsTime) => preSeccondsTime - 1);
        // setT((t) => t - 1);
      }, 1000);
    }
    if (seccondsTime === 0) {
      setSeccondsTime(59);
      minuteRef = setInterval(() => {
        setMinuteTime((preMinute) => preMinute - 1);
      });
      if(minuteTime === 0 && seccondsTime === 0) {
        seccondsRef.clea
      }
    }
  }, []);

  const handleClick = (otp) => {
    if (!otp) {
      logUpContext.setCheckOtp(false);
    } else {
      logUpContext.setCheckOtp(true);
    }
  };

  const validateOtp = (otp) => {
    console.log("otp: " + otp);
    if (otp === undefined) {
      logUpContext.setCheckOtp(false);
    }
    logUpContext.setCheckOtp(true);
  };

  return (
    <div className="main" style={{ backgroundColor: "#f8f9fd" }}>
      {/* {console.log("component render: " + seccondsTime.current)} */}
      <button
        onClick={() => {
          navigate("/enterMail");
        }}
      >
        change EnterMail
      </button>
      {console.log("check Otp: " + logUpContext.checkOtp)}
      <div className="form-pro">
        <div className="form-vip">
          <div className="banner">
            <img src={bannerForm} alt="banner-from"></img>
          </div>
          <div className="form-detail">
            <h2>Xin hãy nhập Otp</h2>
            <form
              className={clsx("needs-validation", {
                "was-validated": logUpContext.checkOtp,
              })}
              noValidate
              onSubmit={(e) => logUpContext.onSubmitEvent(e)}
            >
              <div className="form-group">
                <label htmlFor="Tên đầy đủ">Nhập Otp:</label>
                <input
                  type="text"
                  className="form-control"
                  id="text"
                  placeholder="Nhập otp"
                  name="text"
                  required
                  onChange={(e) => {
                    logUpContext.setOtp(e.target.value);
                    console.log(logUpContext.otp);
                  }}
                />
                <div className="valid-feedback">Valid.</div>
                <div className="invalid-feedback">Vui lòng điền thông tin.</div>
              </div>
              <div className="time-countdown">
                Vui lòng chờ {minuteTime}:{seccondsTime} để gửi lại
              </div>
              <button
                style={{ width: "100%", color: "white" }}
                type="submit"
                className="btn btn-vip"
                onClick={() => {
                  // sendOtpWithConditional();
                  validateOtp(logUpContext.otp);
                  console.log("clicked");
                }}
              >
                <i className="fas fa-arrow-right" /> Nhập OTP
              </button>
              <div className="interaction">
                <h6>Đăng nhập với</h6>
                <div className="social">
                  <img src={ggIcon} alt=""></img>
                  <img src={fbIcon} alt=""></img>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
