import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const initialState = {
  statusLogUp: "",
  isLoading: true,
  error: "",
};

const logUpSlice = createSlice({
  name: "logUp",
  initialState,
  reducers: {
    getStart: (state) => {
      state.isLoading = true;
    },
    getSuccess: (state, action) => {
      state.isLoading = false;
      state.statusLogUp = action.payload;
      console.log("reducer: sent");
    },
    getError: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },
  },
});

export const { getStart, getError, getSuccess } = logUpSlice.actions;

export const sendOtp =
  ({ email }) =>
  async (dispatch) => {
    console.log("email reducer:" + email);
    dispatch(getStart());
    let formData = new FormData(); //formdata object
    formData.append("email", email); //append the values with key, value pair

    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    try {
      dispatch(getStart);
      const res = await axios.post(
        "http://localhost:3000/logup/sendOtp",
        formData,
        config
      );
      dispatch(getSuccess(res.data));
    } catch (err) {
      dispatch(getError(err));
    }
  };

export const checkOtp =
  ({ email, otp }) =>
  async (dispatch) => {
    let formData = new FormData(); //formdata object
    formData.append("email", email); //append the values with key, value pair
    formData.append("otp", otp); //append the values with key, value pair
    console

    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    try {
      dispatch(getStart);
      const res = await axios.post(
        "http://localhost:3000/logup/checkOtp/",
        formData,
        config
      );
      dispatch(getSuccess(res.data));
    } catch (err) {
      dispatch(getError(err));
    }
  };

export const logUp =
  ({ otp, email, fullName }) =>
  async (dispatch) => {
    let formData = new FormData(); //formdata object
    formData.append("fullName", fullName);
    formData.append("email", email);
    formData.append("otp", otp);
    //append the values with key, value pair

    const config = {
      headers: { "content-type": "multipart/form-data" },
    };

    try {
      dispatch(getStart);
      const res = await axios.post(
        "http://localhost:3000/logup/checkOtp/",
        formData,
        config
      );
      dispatch(getSuccess(res.data));
    } catch (err) {
      dispatch(getError(err));
    }
  };

export default logUpSlice.reducer;
