    var format = /[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?]+/;
    console.log("begin checkSpecialChar");
    if (format.test(text)) {
      // console.log(true);
      return true;
    } else {
      // console.log(false);
      return false;
    }
