import React, { useContext, useEffect } from "react";
import { LogUpContext } from "../../contexts/LogUp.Context";
import bannerForm from "../../assets/images/banners/bannerForm.png";
import fbIcon from "../../assets/icons/fb.png";
import ggIcon from "../../assets/icons/gg.png";
import clsx from "clsx";
import { useNavigate, useLocation } from "react-router-dom";

export default function EnterMail({ sendOtp }) {
  let navigate = useNavigate();

  const logUpContext = useContext(LogUpContext);
  useEffect(() => {
    if(localStorage.getItem("sendOtp")){
      navi
    }

  })

  const sendOtpWithConditional = () => {
    logUpContext.setCheckEmail(logUpContext.isEmail(logUpContext.email));
    if (logUpContext.isEmail(logUpContext.email)) {
      sendOtp({ email: logUpContext.email });
      localStorage.setItem("enterMail", true);
      localStorage.setItem("email", logUpContext.email);
      navigate("/enterOtp");
    } else {
      logUpContext.setCheckEmail(false);
    }
  };

  return (
    <div className="main" style={{ backgroundColor: "#f8f9fd" }}>
      <button onClick={() => navigate("/enterOtp", { replace: true })}>
        Change Otp
      </button>
      {console.log(logUpContext.isEmail(logUpContext.email))}
      <div className="form-pro">
        <div className="form-vip">
          <div className="banner">
            <img src={bannerForm} alt="banner-from"></img>
          </div>
          <div className="form-detail">
            <h2>Vui lòng nhập Email</h2>
            <form
              className={clsx("needs-validation", {
                "was-validated": !logUpContext.checkEmail,
              })}
              // className="needs-validation"
              noValidate
              onSubmit={(e) => logUpContext.onSubmitEvent(e)}
            >
              <div className="form-group">
                <label htmlFor="Tên đầy đủ">Nhập email:</label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  placeholder="Nhập email"
                  name="email"
                  required
                  onChange={(e) => {
                    logUpContext.setEmail(e.target.value);
                  }}
                />
                <div className="valid-feedback">Valid.</div>
                <div className="invalid-feedback">
                  Vui lòng điền đúng định dạng email.
                </div>
              </div>
              <button
                style={{ width: "100%", color: "white" }}
                type="submit"
                className="btn btn-vip"
                onClick={() => {
                  sendOtpWithConditional();
                  console.log("clicked");
                }}
              >
                <i className="fas fa-arrow-right" /> Nhận OTP
              </button>
              <div className="interaction">
                <h6>Đăng nhập với</h6>
                <div className="social">
                  <img src={ggIcon} alt=""></img>
                  <img src={fbIcon} alt=""></img>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
