import React, { useState } from "react";
import threeFor from "../assets/icons/3:4.png";
import oneTwo from "../assets/icons/1:2.png";
import chinFlip from "../assets/icons/chinFlip.png";
import fullFace from "../assets/icons/fullFace.png";
import glass from "../assets/icons/glass.png";
export default function Category() {
  // const [category, setCategory] = useState([]);
  const categories = [
    { name: "Mũ bảo hiểm 3/4 đầu", id: 1, icon: threeFor, link: "" },
    { name: "Mũ bảo hiểm Fullface", id: 2, icon: fullFace, link: "" },
    { name: "Mũ bảo hiểm lật cằm", id: 3, icon: chinFlip, link: "" },
    { name: "Mũ bảo hiểm 1/2 đầu", id: 4, icon: oneTwo, link: "" },
    { name: "Kính Mũ bảo hiểm", id: 5, icon: glass, link: "" },
  ];

  const prices = [
    { name: "Dưới 500.000 đ", price: "" },
    { name: "Từ 500.000 đ - 700.000 đ", price: "" },
    { name: "Từ 700.000 đ - 100.000 đ", price: "" },
    { name: "Trên 1.000.000 đ", price: "" },
  ];
  const abouts = [
    { name: "Giới thiệu về JunHelmet", id: 1, icon: "", link: "" },
    { name: "Danh sách đại lý", id: 2, icon: "", link: "" },
    { name: "Chính sách và quy định", id: 3, icon: "", link: "" },
    { name: "Liên hệ", id: 4, icon: "", link: "" },
  ];
  return (
    <div className="row">
      <div className="col-sm-4">
        <div className="category">
          <div className="category-item">
            <h4>Danh mục</h4>
            <ul>
              {categories.map((item) => (
                <li key={item.id}>
                  <img src={item.icon} alt="key" />
                  <span>{item.name}</span>
                </li>
              ))}
            </ul>
          </div>
          <div className="category-item">
            <h4>Khoảng giá</h4>
            <ul>
              {prices.map((item) => (
                <li key={item.name}>{item.name}</li>
              ))}
            </ul>
          </div>
          <div className="category-item">
            <h4>Về chúng tôi</h4>
            <ul>
              {/* {abouts.map((item) => (
                <li key={item.name}>{item.name}</li>
              ))} */}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}
